import React from "react";

function Contact() {
  const handleChange = (e) => {
    console.log(e);
  };

  const validateEmail = (email) => {
    var re =
      /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return re.test(String(email).toLowerCase());
  };

  return (
    <div>
      <p>Contact</p>
      <form class="row row-cols-lg-auto g-3 align-items-center">
        <input
          type="text"
          class="form-control"
          placeholder="name"
          aria-label="Name"
          name="name"
        ></input>
        <input
          type="text"
          class="form-control"
          placeholder="email"
          aria-label="email"
          name="email"
          onChange={handleChange}
        ></input>
        <input
          type="text"
          class="form-control"
          placeholder="message"
          aria-label="message"
          name="message"
        ></input>
        <button>Contact Me</button>
      </form>
    </div>
  );
}

export default Contact;
